customer_code = input("Enter customer code (R C or I): ").upper(); print("Invalid input (customer code)") if customer_code not in ['R', 'C', 'I'] else (lambda beginning_reading, ending_reading: print("Invalid input (beginning or ending reading value is out of the range)") if not 0 <= beginning_reading <= 999999999 or not 0 <= ending_reading <= 999999999 else (lambda gallons_used: print(f"Customer code: {customer_code}\nBeginning meter reading: {beginning_reading:09}\nEnding meter reading: {ending_reading:09}\nGallons of water used: {gallons_used:.1f}\nAmount billed: ${(5.00 + 0.0005 * gallons_used) if customer_code == 'R' else (1000.00 if gallons_used <= 4000000 else 1000.00 + 0.00025 * (gallons_used - 4000000)) if customer_code == 'C' else (1000.00 if gallons_used <= 4000000 else 2000.00 if gallons_used <= 10000000 else 2000.00 + 0.00025 * (gallons_used - 10000000)):.2f}"))((ending_reading - beginning_reading) / 10 if ending_reading >= beginning_reading else (999999999 - beginning_reading + ending_reading + 1) / 10))(int(input("Enter beginning reading (between 0 and 999999999): ")), int(input("Enter ending reading (between 0 and 999999999): ")))